unitsize(2 cm);

pair A, B, V;

V = (1,0) + dir(60);
A = (0,0);
B = (2,0);

draw((-0.5,0)--(2.5,0));
draw((0,-1)--(0,1));
draw(Circle((1,0),1),blue);
draw(A--V,red,Arrow(6));
draw(B--V,red,Arrow(6));

label("$A$", A, SW);
label("$B$", B, SE);
label("$V$", V, NE);
[/asy]
<<<END: An example problem and solution>>>

