pair A, B, C, D, E, P, Q;
A=(0,0);
B=(4,0);
C=(4,-4);
D=(0,-4);
E=(2,-3.464);
P=(2.535,-2.535);
Q=(4,-2.535);
draw(A--B--C--D--A--E--B);
draw(A--C);
